package string.concatenate;

public class Concatenate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s = "Sachin";
		String l = "Tendulkar";
		
		String j = s+l;
		System.out.println(j);
		
		String k = s.concat(l);
		System.out.println(k);

	}

}
